mapper.select:
select table_id, student_id, live_benefit, travel_benefit, now_step, apply_state, user_name student_name, sd1.dept_name grade, sd2.dept_name school from benefit_apply ba left outer join sys_user su on ba.student_id = su.user_id left outer join user_info ui on su.user_id = ui.user_id left outer join sys_dept sd1 on su.dept_id = sd1.dept_id left outer join sys_dept sd2 on sd1.parent_id = sd2.dept_id
and table_id = #{tableId}
and student_id = #{studentId}
and sd1.dept_name = '${grade}'
and sd2.dept_name = '${school}'

mapper.update:
update benefit_apply
live_benefit = #{liveBenefit}, travel_benefit = #{travelBenefit}, now_step = #{nowStep}, apply_state = #{applyState},
where table_id = #{tableId}

update benefit_apply
set now_step = #{nextStage}, apply_state = #{nextState}
where table_id = #{applyId}
